export default function HomeScreen() {
  return (
    <ThemedView style={styles.container}>
      <View style={styles.header}>
        <Entypo name="instagram" size={24} color="black" />
        <Text style={styles.headerTitle}>Instagram</Text>
      </View>
      <View style={styles.headerIcons}>
        <Text
          style={{
            fontFamily: "Times New Roman",
            fontSize: 20,
            marginLeft: 5,
            marginRight: 240,
            fontWeight: "bold",
          }}
        >
          _patel0705_
        </Text>
        <TouchableOpacity>
          <FontAwesome name="heart-o" size={24} color="black" />
        </TouchableOpacity>
        <TouchableOpacity>
          <AntDesign name="message1" size={24} color="black" />
        </TouchableOpacity>
      </View>

      <ScrollView
        horizontal
        showsHorizontalScrollIndicator={false}
        style={styles.storiesContainer}
      >
        {stories.map((story) => (
          <TouchableOpacity key={story.id} style={styles.story}>
            <Image source={story.image} style={styles.storyImage} />
            <Text style={styles.storyUser}>{story.user}</Text>
          </TouchableOpacity>
        ))}
      </ScrollView>

      <ScrollView>
        {posts.map((post) => (
          <View key={post.id} style={styles.postContainer}>
            <View style={styles.postHeader}>
              <Image
                source={require("@/assets/images/profile_pic.jpg")}
                style={styles.postProfilePic}
              />
              <Text style={styles.postUser}>{post.user}</Text>
            </View>
            <Image source={post.image} style={styles.postImage} />
            <View style={styles.postFooter}>
              <Text style={styles.postLikes}>{post.likes} likes</Text>
              <Text style={styles.postCaption}>
                <Text style={styles.postUser}>{post.user}</Text> {post.caption}
              </Text>
            </View>
          </View>
        ))}
      </ScrollView>
    </ThemedView>
  );
}

